import React from "react";

class UploadWidget extends React.Component {
  form = () => {
    for (let i = 0; i < this.state.add; i++) {
      return (
        <form action="/product" enctype="multipart/form-data" method="post">
          <input type="file" name="file-to-upload"></input>
          <input type="submit" value="Upload"></input>
        </form>
      );
    }
  };
  constructor(props) {
    super(props);

    this.state = {
      add: 0,
    };
    console.log(this.state,"at constructor");
  }
  handleAddition = () => {
    this.setState({
      add: this.state.add + 1,
    });
  };

  render() {
    return (
      <div>
        {this.form()}
        {this.state.add == 1 ? (
          <form action="/product" enctype="multipart/form-data" method="post">
            <input type="file" name="file-to-upload"></input>
            <input type="submit" value="Upload"></input>
          </form>
        ) : (
          <div></div>
        )}

        <button
          type="button"
          className="make-button-link"
          data-add-button=""
          href="#"
          onClick={this.handleAddition}
        >
          addmore
        </button>
      </div>
    );
  }
}

export default UploadWidget;
